  class Piece {
  constructor(piece, team) {
    this.team = team
    this.piece=piece
    switch (piece) {
      case "pawn":
        this.firstTurn = true
        this.validTurns = (pos=this.pos) => {
        this.pos=pos
          let result = []
          let offset
          let y=this.pos[0]
          let x = this.pos[1]
          if (this.team == "white") {
            offset = 1
          } else {
             offset = -1
          }
         if (board.data[y + 1 * offset][x - 1]){
          if (board.data[y + 1 * offset][x - 1].team != this.team){
            result.push([y + 1 * offset,x - 1])
          }}        
          if(board.data[y + 1 * offset][x + 1]){
          if (board.data[y + 1 * offset][x + 1].team != this.team) {
            result.push([ y + 1 * offset],x + 1)
          }}
          if (board.data[y + 2 * offset][x] == "") {
            result.push([y + 2 * offset,x])
          }
          if (board.data[y + 1 * offset][x] == "") {
            result.push([y + 1 * offset,x])
          }
          return result
        }
        break
      default:
        break
    }
  }
}
