function main() {
  var removeEverything = () => {
    var elements = document.body.querySelectorAll("*")
    for (var i = 0; i < elements.length; i++) {
      elements[0].remove()
    }
  }

  
  var selectedPiece = ""
  function selectPiece(element) {
    var pos = element.id.split(";")
    pos[0]=Number(pos[0])
    pos[1]=Number(pos[1])
     var piece = board.data[pos[0]][pos[1]]
     
    if (selectedPiece){
    if (selectedPiece==piece){
      for (let i of selectedPiece.validTurns(pos)) {
     document.getElementById(`${i[0]};${i[1]}`).style.backgroundColor="";  
    }
    	selectedPiece="";
    }else{
    if ( selectedPiece.validTurns().some(r=>JSON.stringify(r)==JSON.stringify(pos))){
    for (let i of selectedPiece.validTurns(pos)) {
     document.getElementById(`${i[0]};${i[1]}`).style.backgroundColor="";  
    }
    board.move(selectedPiece, pos)
    }}
    }else{
   
		
    for (let i of piece.validTurns(pos)) {
     document.getElementById(`${i[0]};${i[1]}`).style.backgroundColor="blue";  
    }
    selectedPiece=piece;
  }
  updateDisplay()
 }

  function createChessBoard() {
    let chessBox = document.createElement("div")
    chessBox.id = "chessBox"
    chessBox.style =
      "border-style:solid; border-width:1px; display: grid; grid-template-columns: repeat(8,60px); grid-template-rows: repeat(8,60px); width: fit-content; height: fit-content;"
    document.body.appendChild(chessBox)

    for (var i = 0; i < 8; i++) {
      for (var o = 0; o < 8; o++) {
        let box = document.createElement("div")
        box.addEventListener("click", (e) => {
          selectPiece(e.target)
        })
        box.style =
          "border-style: solid; border-width:1px; font-size:10px;font-weight: bold; background-size:contain;"
        box.id = `${i};${o}`
        box.textContent = `${i};${o}`
        chessBox.append(box)
      }
    }
  }



  board = new Board()	
  board.data[3][3]=new Piece("pawn" ,"white")
 board.data[3][3]=""
  createChessBoard()
  removeEverything()
  updateDisplay()
}
